mutation addProductsToCart($cartId: String!) {
	addProductsToCart(
		cartId: $cartId
		cartItems: [
			{
				quantity: 2
				sku: "8722174"
			}
		]
	) {
		cart {
			itemsV2 {
				items {
					uid
					quantity
					prices{
						price_including_tax{
							currency
							value
						}
					}
				}
			}
		}
		user_errors {
			code
			message
		}
	}
}

query cart($cartId: String!) {
	cart(
		cart_id: $cartId #refresh_cart: true
	) {
		email
		total_quantity
		prices {
			discounts {
				label
				amount {
					value
					currency
				}
			}
			subtotal_including_tax {
				value
			}
			subtotal_excluding_tax {
				value
			}
			subtotal_with_discount_excluding_tax {
				value
			}
			grand_total {
				value
			}
		}
		itemsV2 {
			items {
				cost_center
				sku_alias

				quantity
				uid
				id
				product {
					uid
					name
					sku
					#only_x_left_in_stock
					price {
						regularPrice {
							amount {
								currency
								value
							}
						}
						minimalPrice {
							amount {
								value
								currency
							}
						}
					}
				}
				... on ConfigurableCartItem {
					id
					quantity
					uid
					configurable_options {
						configurable_product_option_uid
						configurable_product_option_value_uid
						id
						option_label
						value_id
						value_label
					}
				}
				prices {
					price {
						currency
						value
					}
					price_including_tax {
						value
					}
					row_total_including_tax {
						currency
						value
					}
					total_item_discount {
						value
					}
					row_total {
						currency
						value
					}
					discounts {
						label
						amount {
							value
						}
					}
				}
			}
		}
	}
}

